sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

	var FIELDNAME = 'u_what_needed';

	// Get object
    var whatNeeded = g_form.getValue(FIELDNAME);

	// Clear all choices fro the What needed field choice list
	g_form.clearOptions(FIELDNAME);

	// Add in choices according to the What needed field
	if (newValue === 'hr') {
		g_form.addOption(FIELDNAME, 'hr1', 'Human Resource 1');
		g_form.addOption(FIELDNAME, 'hr2', 'Human Resource 2');
	}

	if (newValue === 'facilities') {
		g_form.addOption(FIELDNAME, 'facilities1', 'Facilities 1');
		g_form.addOption(FIELDNAME, 'facilities2', 'Facilities 2');
	}

	if (newValue === 'legal') {
		g_form.addOption(FIELDNAME, 'legal1', 'Legal 1');
		g_form.addOption(FIELDNAME, 'legal2', 'Legal 2');
	}

	g_form.addOption(FIELDNAME, 'other', 'Other');

}
